import React from 'react';
import ReactDOM from 'react-dom';

class Ramadhani extends React.Component {
  constructor() {
    super();
    this.state = {
      color: ''
    };
  }

  componentDidMount() {
    this.setState({ color: this.props.warna || 'orange' }, () => {
      console.log('udah berubah')
    });  
  }

  componentWillUnmount() {

  }

  render() {
    return <h2 style={{ color: this.state.color }} >Ramadhani</h2>
  }
}


ReactDOM.render(<Ramadhani warna="green"/>, document.getElementById('app'));







