import React from 'react';
import ReactDOM from 'react-dom';

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            id: 0,
            input: '',
        }
        this.inputField = this.inputField.bind(this)
        this.ketikaClick = this.ketikaClick.bind(this)
        this.hapusList = this.hapusList.bind(this)
    }

    inputField(e) {
        this.setState( {input: e.target.value} )
    }

    ketikaClick() {
        this.setState( {id: this.state.id+1} )
        this.props.lists.push(this.state)
        this.setState( {input: ''} )
    }

    hapusList(id) {
        lists.splice(id, 1)
        this.setState( {input: ''} )
        console.log(id)
    }

    render() {
        return (
            <div>
                <input onChange={this.inputField} value={this.state.input} style={{ width: 300 }}/><br />
                {lists.length < 10 &&
                    <button onClick={this.ketikaClick} style={{ marginLeft: 264 }}>Add</button>
                }
                <ListItems lists={lists} hapusList={this.hapusList} />
            </div>
        )
    }
}

class ListItems extends React.Component {
    constructor() {
        super()
        this.hapusList = this.hapusList.bind(this)
    }

    hapusList(e) {
        const test = e.target.parentNode.id
        console.log(test)
    }
    render() {
        const lists = this.props.lists;
        const listItems = lists.map((list) => 
        <li key={list.id} id={list.id} >
            {list.input} <button key={list.id} onClick={this.hapusList}>x</button>
        </li>

        )
        return (
            <div>
                {lists.length < 10 &&
                    <h1 style={{color: 'green'}}>{lists.length}</h1>
                }
                {lists.length >= 10 &&
                    <h1 style={{color: 'red'}}>{lists.length} Sudah terlalu banyak yang harus dikerjakan</h1>
                }
                <ul>
                    {listItems}
                </ul>
            </div>            
        )
    }
}

const lists = []

ReactDOM.render(<App lists={lists} />, document.getElementById('app'))
































