import React from 'react';
import ReactDOM from 'react-dom';


class Compiler extends React.Component {
    constructor() {
        super()
        // this.state = {
        //     nama: 'Ramad',
        // }
        this.ketikaBerubah = this.ketikaBerubah.bind(this)
    }

    ketikaBerubah(e) {
        this.setState({nama: e.target.value})
    }

    render() {
        return (
            <div>
                <h1>{this.state.nama}</h1>
                <input onChange={this.ketikaBerubah} color={this.state.color} />
                <GantiInput handleClick={this.state.nama} />
            </div>
        )
    }
}

class GantiInput extends React.Component {
    constructor() {
        super()
        this.state = {
            nama: 'Ramad',
        }
        this.ketikaBerubah = this.ketikaBerubah.bind(this)
    }

    ketikaClick() {
    }

    ketikaBerubah(e) {
        this.state.nama = e.target.value;
    }

    render() {
        return (
            <div>
                <h1>{this.state.nama}</h1>
                <input onChange={this.ketikaBerubah} />
                <button onClick={this.ketikaClick} ></button>
                {
                    console.log(this.props.handleClick)
                }
            </div>
        )
    }
}



ReactDOM.render(<Compiler />, document.getElementById('app'))
