import React from 'react';
import ReactDOM from 'react-dom';

const divStyle = {
}

class App extends React.Component {
  render () {
    return (
      <div style={{display: 'flex', justifyContent: 'space-around'}}>
        <FormBarang />
        <FormBelanja />
      </div>
    )
  };
};


class FormBarang extends React.Component {
  constructor() {
    super();
    this.state = {
      input: ''
    };
  };

  _inputBarang(e) {
    this.setState({ input: e.terget.element })
  }

  render() {
    return (
      <form>
        <input onChange={this._inputBarang}></input><br />
        <input></input>
        <select>
          <option value="makanan">Makanan</option>
          <option value="voucher">Voucher</option>
          <option value="kesehatan">Kesehatan</option>
          <option value="sembako">Sembako</option>
        </select><br></br>
        <button>
          Tambah
        </button>
        {console.log(this.state.input)}
      </form>
    );
  };
};

class ListBarang extends React.Component {
    constructor() {
        super()
        this._hapusListHandler = this._hapusListHandler.bind(this)
    }

    _hapusListHandler(id) {
        this.props.hapusList(id)
    }

    _renderTitle() {
        const lists = this.props.lists;
        if (lists.length < 10) {
            return <h1 style={{color: 'green'}}>{lists.length}</h1>;
        }
        return <h1 style={{color: 'red'}}>{lists.length} Sudah terlalu banyak yang harus dikerjakan</h1>
    }

    render() {
        const listItems = this.props.lists.map((list) =>
            <li key={list.id} id={list.id}>
                {list.input} = {list.id} <button key={list.id} onClick={() => this._hapusListHandler(list.id)}>x</button>
            </li>
        );

        return (
            <div>
                {this._renderTitle()}
                <ul>
                    {PILIHAN[this.props.element]}
                    {listItems}
                </ul>
            </div>            
        )
    }
}

class FormBelanja extends React.Component {
  render() {
    return (
      <form>
        <input></input><br />
        <input></input>
        <select>
          <option value="makanan">Makanan</option>
          <option value="voucher">Voucher</option>
          <option value="kesehatan">Kesehatan</option>
          <option value="sembako">Sembako</option>
        </select>
      </form>
    );
  };
};

ReactDOM.render(<App />, document.getElementById('app'));























































