import React from 'react';
import ReactDOM from 'react-dom';

class App extends React.Component {
  render () {
    return (
      <div style={{display: 'flex', justifyContent: 'center'}}>
        <FormBarang/>
        <FormBelanja/>
      </div>
    )
  };
};


class FormBarang extends React.Component {
  constructor() {
    super();
    this.state = {
      barang: '',
      harga: '',
      list: []
    };
    this._inputBarang = this._inputBarang.bind(this);
    this._inputHarga = this._inputHarga.bind(this);
    this._handleSubmit = this._handleSubmit.bind(this);
  };

  _inputBarang(e) {
    this.setState( {barang: e.target.value} );
  };
  _inputHarga(e) {
    this.setState( {harga: e.target.value} );
  };

  _handleSubmit(e) {
    e.preventDefault();
    const newList = this.state.list;
    newList.push({id: +new Date(), barang: this.state.barang, harga: this.state.harga});

    this.setState({
        barang: '',
        harga: '',
        list: newList
    });
    console.log(newList)
  };

  render() {
    return (
      <form onSubmit={this._handleSubmit} style={{border: '2px solid black'}}>
        <div style={{margin: 20}}>
          <input onChange={this._inputBarang} value={this.state.barang} style={{ width: 263 }}></input><br />
          <input onChange={this._inputHarga} value={this.state.harga}></input>
          <select>
            <option value="makanan">Makanan</option>
            <option value="voucher">Voucher</option>
            <option value="kesehatan">Kesehatan</option>
            <option value="sembako">Sembako</option>
          </select><br></br>
          <input type="submit" value="Tambah" />
          <ListBarang lists={this.state.list} />
        </div>
      </form>
    );
  };
};

class ListBarang extends React.Component {
  render() {
    const listItems = this.props.lists.map((list) =>
    <li key={list.id}>
        `{list.barang} harganya {list.harga}`
    </li>
    ); 
    return (
      <ul>{listItems}</ul>
    )
  }
};

class FormBelanja extends React.Component {
  render() {
    return (
      <form onSubmit={this._handleSubmit} style={{borderRight: '2px solid black', borderBottom: '2px solid black', borderTop: '2px solid black'}}>
        <div style={{margin: 20}}>
          <input></input><br />
          <input></input>
          <select>
            <option value="makanan">Makanan</option>
            <option value="voucher">Voucher</option>
            <option value="kesehatan">Kesehatan</option>
            <option value="sembako">Sembako</option>
          </select>
        </div>
      </form>
    );
  };
};

ReactDOM.render(<App />, document.getElementById('app'));
























































