import React, { Children } from 'react';
import ReactDOM from 'react-dom';

import 'materialize-css/dist/css/materialize.min.css';

class Container extends React.Component {
  constructor() {
    super();
    this.state = {
      color: '',
      nama: 'Ramad'
    };
    this._gantiWarna = this._gantiWarna.bind(this);
    this._gantiNama = this._gantiNama.bind(this);
    this._handleClick = this._handleClick.bind(this);

  }

  _gantiWarna(warna) {
    this.setState({ color: warna });
  }

  _gantiNama(nama) {
    this.setState({ nama: nama });
  }

  _handleClick(nama) {
    this.setState({ nama: nama});
  }

  componentDidMount() {
    this.setState({ color: this.props.warna || 'orange' });  
  }

  componentWillUnmount() {

  }

  render() {
    return <div>
      <h2 style={{ color: this.state.color }}>{this.state.nama}</h2>
      <button onClick={() => this._gantiWarna('red')}>Ganti Merah</button>
      <button onClick={() => this._gantiWarna('yellow')}>Ganti Kuning</button>
      <Kepala color={this.state.color} fieldHandler={this._gantiNama} />
      <GantiInputNama color={this.state.color} fieldHandler={this._handleClick}/>
    </div>;
  }
}

  const Kepala = (props) => {
    const ketikaBerubah = (e) => {
      props.fieldHandler(e.target.value)
    }

  return <div style={{ backgroundColor: props.color }}>
    <input onChange={ketikaBerubah} />
    <button>Ganti Nama</button>
  </div>;
  }

  const GantiInputNama = (props) => {
    const ketikaBerubah = (e) => {
      props.fieldHandler(e.target.value)
      console.log(props.fieldHandler)
    }

    const inputHandler = (e) => {
      console.log(e.target.value)
    }
    return (
      <div style={{ backgroundColor: props.color }}>
        <input onChange={inputHandler} />
        <button onClick={ketikaBerubah}>Ganti Nama</button>
      </div>
    )
  }



