import React from 'react';
import ReactDOM from 'react-dom';


class Compiler extends React.Component {
    constructor() {
        super()
        this.state = {
            nama: 'Ramad',
        }
        this.ketikaBerubah = this.ketikaBerubah.bind(this)
    }



    render() {
        return (
            <div>
                <h1>{this.state.nama}</h1>
                <input onChange={this.ketikaBerubah} color={this.state.color} /> <br />
            </div>
        )
    }
}

class GantiInput extends React.Component {
    constructor() {
        super()
        this.state = {
            nama: 'Ramad',
        }
    }

    ketikaBerubah(e) {
        this.setState({nama: e.target.value})
    }


    render() {
        return (
            <div>

            </div>
        )
    }
}



ReactDOM.render(<Compiler />, document.getElementById('app'))
