  constructor() {
    super()
    this.state = {
      nama: 'ramadh',

    }
    this._inputHandler = this._inputHandler.bind(this)
    this._ketikaBerubah = this._ketikaBerubah.bind(this)
  }

  _ketikaBerubah() {
    this.props.fieldHandler(this.state.nama)
  }

  _inputHandler(e) {
    this.setState( {nama: e.target.value} );
  }

  render() {
    return (
      <div style={{ backgroundColor: this.props.color }}>
        <input onChange={this._inputHandler} />
        <button onClick={this._ketikaBerubah}>Ganti Nama</button>
      </div>
    )
  }
}

