import React from 'react';
import ReactDOM from 'react-dom';

// const digimons = [
//     'https://i.pinimg.com/originals/6e/8d/e3/6e8de392e06f87ab34e407277cab2561.png',
//     'https://vignette.wikia.nocookie.net/digimon/images/a/a8/Biyomon_dwds.png/revision/latest/scale-to-width-down/340?cb=20140727224901',
//     'https://dmowiki.com/images/thumb/e/e5/Kunemon.png/250px-Kunemon.png'
// ];

class Aplikasi extends React.Component {
    constructor() {
        super();
        this.state = {
            digimons: []
        }
    }

    tambahDigimon() {
        // digimons.push('https://i.pinimg.com/originals/6e/8d/e3/6e8de392e06f87ab34e407277cab2561.png');
    }

    render() {
        console.log(this.state.digimons);
        return (
            <div>
                <button onClick={this.tambahDigimon} >Tambah Digimon</button>
                {this.state.digimons.map(digimon => {
                    return <Digimon key={digimon} digimonAvatar={digimon} />;
                })}
            </div>
        );
    }
}

class Digimon extends React.Component {
    render() {
        return (
            <div>
                <h3>Nama Digimon</h3>
                <img width="80" src={this.props.digimonAvatar}/>
            </div>
        )
    }
}

ReactDOM.render(<Aplikasi ke="20" />, document.getElementById('app'));
















