import React from 'react';
import ReactDOM from 'react-dom';

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            id: 0,
            input: '',
        }
        this.inputField = this.inputField.bind(this)
        this.ketikaClick = this.ketikaClick.bind(this)
    }

    inputField(e) {
        this.setState( {input: e.target.value} )
    }

    ketikaClick() {
        this.setState( {id: this.state.id+1} )
        this.props.lists.push(this.state)
        this.setState( {input: ''} )
    }

    render() {
        return (
            <div>
                <input onChange={this.inputField} value={this.state.input} style={{ width: 300 }}/><br />
                {lists.length < 10 &&
                    <button onClick={this.ketikaClick} style={{ marginLeft: 264 }}>Add</button>
                }
                <ListItem lists={this.props.lists} />
            </div>
        )
    }
}

function ListItem(props) {
    const lists = props.lists;
    const listItems = lists.map((list) => 
    <li key={list.id}>{list.input} <button>x</button></li>
    )
    // {console.log(lists)}

    return (
        <div>
            {lists.length < 10 &&
                <h1 style={{color: 'green'}}>{lists.length}</h1>
            }
            {lists.length >= 10 &&
                <h1 style={{color: 'red'}}>{lists.length} Sudah terlalu banyak yang harus dikerjakan</h1>
            }
            {lists.length >=1 &&
                <div>
                    <ul>{listItems}</ul>
                </div>
            }
        </div>
    )
}

const lists = []

ReactDOM.render(<App lists={lists} />, document.getElementById('app'))
































