import React from 'react';
import ReactDOM from 'react-dom';


class Compiler extends React.Component {
    constructor() {
        super()
        this.state = {
            nama: 'Ramad',
        }
        this.ketikaBerubah = this.ketikaBerubah.bind(this)
        this.handleClick = this.handleClick.bind(this)
    }

    gantiNama(nama) {
        this.setState({ nama: nama })
    }

    ketikaBerubah(e) {
        this.gantiNama(e.target.value)
    }

    handleClick(angka) {
        this.gantiNama(angka)
    }

    render() {
        return (
            <div>
                <h1>{this.state.nama}</h1>
                <input onChange={this.ketikaBerubah} color={this.state.color} />
                <GantiInput passClick={this.handleClick} nama={this.state.nama} />
            </div>
        )
    }
}

class GantiInput extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            input: 'Ramadasa',
        }
        this.ketikaBerubah = this.ketikaBerubah.bind(this)
        this.ketikaClick = this.ketikaClick.bind(this)
    }

    ketikaClick() {
        // console.log(this.state.input)
        // this.setState({input: this.state.nama})
        this.props.passClick(this.state.input)
    }

    ketikaBerubah(e) {
        this.setState({input: e.target.value})
    }

    render() {
        // this.props.passClick(this.state.input)
        return (
            <div>
                <input onChange={this.ketikaBerubah} /><br></br>
                <button onClick={this.ketikaClick} >Berubah</button>
            </div>
        )
    }
}



ReactDOM.render(<Compiler />, document.getElementById('app'))
