import React from 'react';
import ReactDOM from 'react-dom';

class App extends React.Component {
  render () {
    return (
      <div style={{display: 'flex', justifyContent: 'space-around'}}>
        <FormBarang/>
        {/* <FormBelanja /> */}
      </div>
    )
  };
};


class FormBarang extends React.Component {
  constructor() {
    super();
    this.state = {
      input: '',
      harga: '',
      list: []
    };
    this._inputBarang = this._inputBarang.bind(this);
    this._inputHarga = this._inputHarga.bind(this);
    this._onSubmit = this._onSubmit(this);
  };

  _inputBarang(e) {
    this.setState( {input: e.target.value} );
  };
  _inputHarga(e) {
    this.setState( {input: e.target.value} );
  };

  _onSubmit() {
    const newList = this.state.list;
    // newList.push({id: +new Date(), input: this.state.input, harga: this.state.harga});

    this.setState({
        input: '',
        harga: '',
        list: newList
    });
  };

  render() {
    return (
      <form>
        <input onChange={this._inputBarang}></input><br />
        <input onChange={this._inputBarang}></input>
        <select>
          <option value="makanan">Makanan</option>
          <option value="voucher">Voucher</option>
          <option value="kesehatan">Kesehatan</option>
          <option value="sembako">Sembako</option>
        </select><br></br>
        <button onClick={this._onSubmit}>
          Tambah
        </button>
        {console.log(this.state.input, this.state.harga, this.state.list)}
        {/* <LIstBarang lists={this.state.list} /> */}
      </form>
    );
  };
};

