import React from 'react';
import ReactDOM from 'react-dom';

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            input: '',
            list: []
        }
        this.inputField = this.inputField.bind(this)
        this._onSubmit = this._onSubmit.bind(this)
        this._handleEnterKey = this._handleEnterKey.bind(this)
        this.hapusList = this.hapusList.bind(this)
    }

    // handle enter key
    _handleEnterKey(e) {
        if(e.key.toLowerCase() === 'enter') {
            this._onSubmit();
        }
    }

    inputField(e) {
        this.setState( {input: e.target.value} );
    }

    _onSubmit() {
        const newList = this.state.list;
        newList.push({id: +new Date(), input: this.state.input});

        this.setState((state) => ({
            input: '',
            list: newList
        }));

        // this.props.list.push(this.state);

        // in salah
        // this.setState(
        //     {
        //         id: this.state.id + 1,
        //         input: '',
        //     } 
        // );
    }

    hapusList(id) {
        const newList = this.state.list;
        const cariIndex = newList.findIndex(e => e.id === id);
        if (cariIndex > -1) {
            newList.splice(cariIndex, 1);

            this.setState({
                list: newList
            });
        }
    }

    render() {
        return (
            <div>
                <input onChange={this.inputField} onKeyPress={this._handleEnterKey} value={this.state.input} style={{ width: 300 }}/><br />
                {this.state.list.length < 10 &&
                    <button onClick={this._onSubmit} style={{ marginLeft: 264 }}>Add</button>
                }
                <ListItems lists={this.state.list} hapusList={this.hapusList} />
            </div>
        )
    }
}

class ListItems extends React.Component {
    constructor() {
        super()
        this.hapusListHandler = this.hapusListHandler.bind(this)
    }

    hapusListHandler(id) {
        // const test = e.target.parentNode.id
        this.props.hapusList(id)
    }

    render() {
        const lists = this.props.lists;
        const listItems = lists.map((list) => 
        <li key={list.id} id={list.id} >
            {list.input} = {list.id} <button key={list.id} onClick={() => this.hapusListHandler(list.id)}>x</button>
        </li>

        )
        return (
            <div>
                {lists.length < 10 &&
                    <h1 style={{color: 'green'}}>{lists.length}</h1>
                }
                {lists.length >= 10 &&
                    <h1 style={{color: 'red'}}>{lists.length} Sudah terlalu banyak yang harus dikerjakan</h1>
                }
                <ul>
                    {listItems}
                </ul>
            </div>            
        )
    }
}

// const listsss = []

ReactDOM.render(<App />, document.getElementById('app'))
































