import React, { Fragment } from 'react';
import ReactDOM from 'react-dom';

const LIST_BARANG_STORAGE = 'list-barang';
const LIST_BELANJA_STORAGE = 'list-belanja';

const getLocalStorageBarang = () => {
    const storage = localStorage.getItem(LIST_BARANG_STORAGE);
    if (!storage) return [];
    return JSON.parse(storage);
};

const saveToLocalStorageBarang = (items) => {
    localStorage.setItem(LIST_BARANG_STORAGE, JSON.stringify(items));
};

const getLocalStorageBelanja = () => {
  const storage = localStorage.getItem(LIST_BELANJA_STORAGE);
  if (!storage) return [];
  return JSON.parse(storage);
};

const saveToLocalStorage = (items) => {
  localStorage.getItem(LIST_BELANJA_STORAGE)
}


class App extends React.Component {
  constructor() {
    super()
    this.state = {
      listBarang: getLocalStorageBarang(),
    };
    this._simpanBarang = this._simpanBarang.bind(this);
    this._hapusList = this._hapusList.bind(this);
  };

  _simpanBarang(barang, harga) {
    var listBaru = this.state.listBarang
    listBaru.push({
      barang,
      harga,
      id: +new Date()
    });

    saveToLocalStorageBarang(listBaru);

    this.setState( {
      list: listBaru
    });
  };

  _hapusList(id) {
    const newList = this.state.listBarang;
    const cariIndex = newList.findIndex(e => e.id === id);
    if (cariIndex > -1) {
      newList.splice(cariIndex, 1);
      this.setState({
            list: newList
      });
    };
    saveToLocalStorageBarang(newList);
  };

  render () {
    return (
      <Fragment>
        <div style={{display: 'flex', justifyContent: 'center'}}>
          <FormBarang submitBarang={this._simpanBarang} />
          <FormBelanja lists={this.state.listBarang} />
        </div>
        <div style={{marginLeft: 300}}>
          <ListBarang lists={this.state.listBarang} hapusList={this._hapusList} />
          <ListBelanja listBarang={this.props.listBarang} listBelanja={this.state.listBelanja} />
        </div>
      </Fragment>

    )
  };
};


class FormBarang extends React.Component {
  constructor() {
    super();
    this.state = {
      barang: '',
      harga: '',
    };
    this._inputBarang = this._inputBarang.bind(this);
    this._inputHarga = this._inputHarga.bind(this);
    this._handleSubmit = this._handleSubmit.bind(this);
  };

  _inputBarang(e) {
    this.setState( {barang: e.target.value} );
  };

  _inputHarga(e) {
    this.setState( {harga: e.target.value} );
  };

  _handleSubmit(e) {
    e.preventDefault();
    this.props.submitBarang(this.state.barang, this.state.harga);
    this.setState({
      barang: '',
      harga: '',
    });
  };

  render() {
    return (
      <div>
        <form onSubmit={this._handleSubmit} style={{border: '2px solid black'}}>
          <h1>Form Barang</h1>
          <div style={{margin: 20}}>
            <input
              onChange={this._inputBarang}
              value={this.state.barang}
              style={{ width: 263 }}
            /><br />
            <input onChange={this._inputHarga} value={this.state.harga}/>
            <select>
              <option value="makanan">Makanan</option>
              <option value="voucher">Voucher</option>
              <option value="kesehatan">Kesehatan</option>
