  };

  render() {
    const listBarang = this.props.lists.map((list) =>
    <li key={list.id}>
        <b>{list.barang}</b> harganya <b>{list.harga}</b> <button onClick={() => this._hapusListHandler(list.id)}>x</button>
    </li>
    ); 
