    );
  };
};

class FormBelanja extends React.Component {
  constructor() {
    super()
    this.state = {
      count: '',
      barang: '',
      listBelanja: []
    };

    this._inputCount = this._inputCount.bind(this);
    this._inputBarang = this._inputBarang.bind(this);
    this._handleSubmit = this._handleSubmit.bind(this);
  };

  _inputCount(e) {
    this.setState( {count: e.target.value} )
  };

  _inputBarang(e) {
    this.setState( {barang: e.target.value} )
  };

  _handleSubmit(e) {
    e.preventDefault();
    const newList = this.state.listBelanja;
    newList.push({id: +new Date(), idBarang: this.state.barang, count: this.state.count});

    this.setState({
      count: '',
      barang: '',
      listBelanja: newList
    });
  };

  render() {
    const listBarang = this.props.lists.map((list) =>
    <option key={list.id} value={list.id} onClick={this._inputBarang}>
      {list.barang}
    </option>
    );
    return (
      <form onSubmit={this._handleSubmit} style={{border: '2px solid black'}}>
        <h1>Form Belanja</h1>
        <div style={{margin: 20}}>
          <select style={{width: 263}} onClick={this._inputBarang}>
            <option>-</option>
            {listBarang}
          </select>
          <input style={{width: 20}} onChange={this._inputCount}></input>
        </div>
        <input type="submit" value="Tambah" style={{marginLeft: 245}} />
      </form>
    );
  };
};

class ListBelanja extends React.Component {
  // get string nama barang, dengan parameter id
  getNamaBarang(idBarang) {
    const barangFiltered = this.props.listBarang.filter(e => parseInt(e.id) === parseInt(idBarang));
    if (barangFiltered.length > 0) {
      return barangFiltered[0].barang;
    }
    return 'barang tidak ditemukan'
  }

  render() {
    console.log(this.props.listBelanja)
    const listBelanja = this.props.listBelanja.map((list) =>
    <li key={list.idBarang}>
      <b>{this.getNamaBarang(list.idBarang)}</b> harganya <b>{list.harga}</b> jumlanya <b>{list.count}</b>
    </li>
    ); 
    return (
      <ul>{listBelanja}</ul>
    );
  };
};

